import React from 'react';
import { Card, CardContent, Typography, IconButton } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { useStore } from '../store/store';
import { Bar, Doughnut } from 'react-chartjs-2';

const Widget = ({ categoryId, widget }) => {
  const removeWidget = useStore((state) => state.removeWidget);

  const getChartData = () => {
    if (widget.name === 'Cloud Accounts') {
      return {
        labels: ['Connected', 'Not Connected'],
        datasets: [
          {
            label: 'Cloud Accounts',
            data: [widget.data.connected, widget.data.notConnected],
            backgroundColor: ['#36A2EB', '#FF6384'],
          },
        ],
      };
    } else if (widget.name === 'Cloud Account Risk Assessment') {
      return {
        labels: ['Failed', 'Passed'],
        datasets: [
          {
            label: 'Cloud Account Risk Assessment',
            data: [widget.data.failed, widget.data.passed],
            backgroundColor: ['#FF6384', '#36A2EB'],
          },
        ],
      };
    } else if (widget.name === 'Image Risk Assessment') {
      return {
        labels: ['Critical', 'High', 'Medium', 'Low'],
        datasets: [
          {
            label: 'Image Risk Assessment',
            data: [widget.data.critical, widget.data.high, widget.data.medium, widget.data.low],
            backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
          },
        ],
      };
    } else if (widget.name === 'Image Security Issues') {
      return {
        labels: ['Critical', 'High', 'Medium', 'Low'],
        datasets: [
          {
            label: 'Image Security Issues',
            data: [widget.data.critical, widget.data.high, widget.data.medium, widget.data.low],
            backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
          },
        ],
      };
    }

    return null;
  };

  const chartData = getChartData();

  return (
    <Card variant="outlined" sx={{ minWidth: '550px', maxWidth: '700px', height: '300px', width: '550px', position: 'relative' }}>
      <CardContent>
        <Typography variant="h6">{widget.name}</Typography>
        {chartData ? (
          widget.name === 'Cloud Accounts' || widget.name === 'Cloud Account Risk Assessment' ? (
            <Doughnut data={chartData} />
          ) : (
            <Bar data={chartData} />
          )
        ) : (
          <Typography variant="body2">{widget.text}</Typography>
        )}
      </CardContent>
      <IconButton
        sx={{ position: 'absolute', top: '5px', right: '5px' }}
        onClick={() => removeWidget(categoryId, widget.id)}
      >
        <CloseIcon />
      </IconButton>
    </Card>
  );
};

export default Widget;
