import React, { useState } from 'react';
import {
  Tabs,
  Tab,
  List,
  ListItem,
  ListItemText,
  Checkbox,
  Button,
  Box,
} from '@mui/material';

const AddWidgetForm = ({ onClose }) => {
  const [selectedWidgets, setSelectedWidgets] = useState([]);
  const [tabIndex, setTabIndex] = useState(0);

  const handleToggle = (widget) => () => {
    const currentIndex = selectedWidgets.indexOf(widget);
    const newChecked = [...selectedWidgets];

    if (currentIndex === -1) {
      newChecked.push(widget);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setSelectedWidgets(newChecked);
  };

  const handleTabChange = (event, newValue) => {
    setTabIndex(newValue);
  };

  const categories = {
    CSPM: ['Cloud Accounts', 'Cloud Account Risk Assessment'],
    CWPP: ['Top 5 Namespace Specific Alerts', 'Workload Alerts'],
    Image: ['Image Risk Assessment', 'Image Security Issues'],
    Ticket: ['Ticket 1', 'Ticket 2'],
  };

  const currentCategory = Object.keys(categories)[tabIndex];

  return (
    <Box sx={{ width: 400, padding: 2 }}>
      <Tabs
        value={tabIndex}
        onChange={handleTabChange}
        variant="fullWidth"
        textColor="primary"
        indicatorColor="primary"
      >
        {Object.keys(categories).map((category) => (
          <Tab key={category} label={category} />
        ))}
      </Tabs>

      <List>
        {categories[currentCategory].map((widget) => (
          <ListItem key={widget} button onClick={handleToggle(widget)}>
            <Checkbox
              checked={selectedWidgets.indexOf(widget) !== -1}
              tabIndex={-1}
              disableRipple
            />
            <ListItemText primary={widget} />
          </ListItem>
        ))}
      </List>

      <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
        <Button variant="outlined" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="contained" color="primary">
          Confirm
        </Button>
      </Box>
    </Box>
  );
};

export default AddWidgetForm;
