import React, { useState } from 'react';
import { Container, Typography, Drawer, Box } from '@mui/material';
import Dashboard from './components/Dashboard';
import AddWidgetForm from './components/AddWidgetModal'; // Replace AddWidgetModal with AddWidgetForm
import './App.css';

const App = () => {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);

  const openDrawer = () => setIsDrawerOpen(true);
  const closeDrawer = () => setIsDrawerOpen(false);

  return (
    <Container
      maxWidth={false}
      sx={{
        padding: '0px 24px',
        '@media (min-width: 1200px)': {
          padding: '0px 48px',
        },
      }}
    >
      <Typography variant="h4" component="h1" gutterBottom>
        Dynamic Dashboard
      </Typography>
      <Dashboard openModal={openDrawer} />
      <Drawer
        anchor="right"
        open={isDrawerOpen}
        onClose={closeDrawer}
        sx={{ 
          '& .MuiDrawer-paper': { 
            width: 400,  // Adjust the width of the drawer
            padding: '20px',  // Add padding to match the design
          } 
        }}
      >
        <Box>
          <Typography variant="h6" gutterBottom>
            Personalize your dashboard by adding the following widget
          </Typography>
          {/* AddWidgetForm handles the form with widget options */}
          <AddWidgetForm />
        </Box>
      </Drawer>
    </Container>
  );
};

export default App;
