// src/components/Category.js
import React from 'react';
import { Card, CardContent, Typography, Button } from '@mui/material';
import Widget from './Widget';
import './Category.css';  // Add this import for custom styles

const Category = ({ category, openModal }) => {
  return (
    <Card variant="outlined">
      <CardContent>
        <Typography variant="h6" gutterBottom>
          {category.name}
        </Typography>
        <div className="widget-container">
          {category.widgets.length > 0 ? (
            <>
              {category.widgets.map((widget) => (
                <Widget key={widget.id} categoryId={category.id} widget={widget} />
              ))}
              <div className="widget">
                <Button variant="contained" color="white" onClick={openModal} className="add-widget-button">
                  + Add Widget
                </Button>
              </div>
            </>
          ) : (
            <div className="widget">
              <Button variant="contained" color="white" onClick={openModal} className="add-widget-button">
                + Add Widget
              </Button>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default Category;
