// src/components/AddWidgetModal.js
import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, MenuItem } from '@mui/material';
import { useStore } from '../store/store';

const AddWidgetModal = ({ onClose }) => {
  const [widgetName, setWidgetName] = useState('');
  const [widgetText, setWidgetText] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  const { categories, addWidget } = useStore();

  const handleAddWidget = () => {
    if (selectedCategory && widgetName && widgetText) {
      const newWidget = {
        id: Date.now(),
        name: widgetName,
        text: widgetText,
      };
      addWidget(parseInt(selectedCategory), newWidget);
      onClose();
    }
  };

  return (
    <Dialog open={true} onClose={onClose}>
      <DialogTitle>Add Widget</DialogTitle>
      <DialogContent>
        <TextField
          select
          label="Select Category"
          fullWidth
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
          margin="normal"
        >
          {categories.map((category) => (
            <MenuItem key={category.id} value={category.id}>
              {category.name}
            </MenuItem>
          ))}
        </TextField>
        <TextField
          label="Widget Name"
          fullWidth
          value={widgetName}
          onChange={(e) => setWidgetName(e.target.value)}
          margin="normal"
        />
        <TextField
          label="Widget Text"
          fullWidth
          multiline
          rows={4}
          value={widgetText}
          onChange={(e) => setWidgetText(e.target.value)}
          margin="normal"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={handleAddWidget} color="primary" variant="contained">
          Add Widget
        </Button>
        <Button onClick={onClose} color="secondary" variant="contained">
          Cancel
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default AddWidgetModal;
