export const useStore = create((set) => ({
  categories: [
    {
      id: 1,
      name: 'CSPM Executive Dashboard',
      widgets: [
        {
          id: 1,
          name: 'Cloud Accounts',
          text: 'Connected: 2, Not Connected: 2',
          chartData: { Connected: 2, 'Not Connected': 2 }
        },
        {
          id: 2,
          name: 'Cloud Account Risk Assessment',
          text: 'Failed: 1689, Passed: 7251',
          chartData: { Failed: 1689, Passed: 7251 }
        },
      ],
    },
    {
      id: 2,
      name: 'CWPP Dashboard',
      widgets: [],
    },
    {
      id: 3,
      name: 'Registry Scan',
      widgets: [
        {
          id: 3,
          name: 'Image Risk Assessment',
          text: 'Critical: 98, High: 210, Medium: 650, Low: 512',
          chartData: { Critical: 98, High: 210, Medium: 650, Low: 512 }
        },
        {
          id: 4,
          name: 'Image Security Issues',
          text: 'Critical: 1, High: 2, Medium: 3, Low: 4',
          chartData: { Critical: 1, High: 2, Medium: 3, Low: 4 }
        },
      ],
    },
  ],
  addWidget: (categoryId, widget) => set((state) => ({
    categories: state.categories.map((category) =>
      category.id === categoryId
        ? { ...category, widgets: [...category.widgets, widget] }
        : category
    ),
  })),
  removeWidget: (categoryId, widgetId) => set((state) => ({
    categories: state.categories.map((category) =>
      category.id === categoryId
        ? { ...category, widgets: category.widgets.filter((widget) => widget.id !== widgetId) }
        : category
    ),
  })),
}));
