import React from 'react';
import { List, ListItem, ListItemText, Checkbox, Button } from '@mui/material';

const AddWidgetForm = () => {
  const [selectedWidgets, setSelectedWidgets] = React.useState([]);

  const handleToggle = (widget) => () => {
    const currentIndex = selectedWidgets.indexOf(widget);
    const newChecked = [...selectedWidgets];

    if (currentIndex === -1) {
      newChecked.push(widget);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setSelectedWidgets(newChecked);
  };

  const widgets = [
    'Cloud Accounts',
    'Cloud Account Risk Assessment',
    // Add more widget options as necessary
  ];

  return (
    <form>
      <List>
        {widgets.map((widget) => (
          <ListItem key={widget} button onClick={handleToggle(widget)}>
            <Checkbox
              checked={selectedWidgets.indexOf(widget) !== -1}
              tabIndex={-1}
              disableRipple
            />
            <ListItemText primary={widget} />
          </ListItem>
        ))}
      </List>
      <Button variant="contained" color="primary" fullWidth>
        Confirm
      </Button>
    </form>
  );
};

export default AddWidgetForm;
