import React, { useState } from 'react';
import { Tabs, Tab, List, ListItem, ListItemText, Checkbox, Button, Box, TextField, InputAdornment } from '@mui/material';

const AddWidgetForm = ({ onConfirm, onCancel }) => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [selectedWidget, setSelectedWidget] = useState(null);
  const [inputData, setInputData] = useState({
    failed: 0,
    passed: 0,
    critical: 0,
    high: 0,
    medium: 0,
    low: 0,
  });

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
    setSelectedWidget(null); // Reset selected widget when switching tabs
  };

  const handleToggle = (widget) => () => {
    if (selectedWidget === widget) {
      setSelectedWidget(null);
    } else {
      setSelectedWidget(widget);
    }
  };

  const handleInputChange = (name) => (event) => {
    setInputData({
      ...inputData,
      [name]: parseInt(event.target.value, 10),
    });
  };

  const widgetCategories = [
    {
      label: 'CSPM',
      widgets: [
        { name: 'Cloud Accounts', inputs: [] },
        { name: 'Cloud Account Risk Assessment', inputs: ['failed', 'passed'] },
      ],
    },
    {
      label: 'CWPP',
      widgets: [
        { name: 'Top 5 Namespace Specific Alerts', inputs: [] },
        { name: 'Workload Alerts', inputs: [] },
      ],
    },
    {
      label: 'Image',
      widgets: [
        { name: 'Image Risk Assessment', inputs: ['critical', 'high', 'medium', 'low'] },
        { name: 'Image Security Issues', inputs: ['critical', 'high', 'medium', 'low'] },
      ],
    },
    {
      label: 'Ticket',
      widgets: [
        { name: 'Open Tickets', inputs: [] },
        { name: 'Resolved Tickets', inputs: [] },
      ],
    },
  ];

  const handleConfirm = () => {
    if (selectedWidget) {
      const category = widgetCategories[selectedTab].label;
      const widget = {
        id: new Date().getTime(),
        name: selectedWidget,
        text: JSON.stringify(inputData), // Storing input data as text (you may modify how this is stored)
      };
      onConfirm([widget], category); // Pass selected widget and category
    }
  };

  return (
    <Box sx={{ padding: 2, width: '100%' }}>
      <Tabs
        value={selectedTab}
        onChange={handleTabChange}
        variant="fullWidth"
        sx={{ marginBottom: 2 }}
      >
        {widgetCategories.map((category) => (
          <Tab key={category.label} label={category.label} />
        ))}
      </Tabs>

      <List>
        {widgetCategories[selectedTab].widgets.map((widget) => (
          <React.Fragment key={widget.name}>
            <ListItem button onClick={handleToggle(widget.name)}>
              <Checkbox
                checked={selectedWidget === widget.name}
                tabIndex={-1}
                disableRipple
              />
              <ListItemText primary={widget.name} />
            </ListItem>
            {selectedWidget === widget.name && widget.inputs.length > 0 && (
              <Box sx={{ paddingLeft: 4, paddingTop: 2 }}>
                {widget.inputs.includes('failed') && (
                  <TextField
                    label="Failed"
                    type="number"
                    value={inputData.failed}
                    onChange={handleInputChange('failed')}
                    InputProps={{
                      inputProps: { min: 0 },
                      startAdornment: <InputAdornment position="start">Failed:</InputAdornment>,
                    }}
                    fullWidth
                    margin="normal"
                  />
                )}
                {widget.inputs.includes('passed') && (
                  <TextField
                    label="Passed"
                    type="number"
                    value={inputData.passed}
                    onChange={handleInputChange('passed')}
                    InputProps={{
                      inputProps: { min: 0 },
                      startAdornment: <InputAdornment position="start">Passed:</InputAdornment>,
                    }}
                    fullWidth
                    margin="normal"
                  />
                )}
                {widget.inputs.includes('critical') && (
                  <TextField
                    label="Critical"
                    type="number"
                    value={inputData.critical}
                    onChange={handleInputChange('critical')}
                    InputProps={{
                      inputProps: { min: 0 },
                      startAdornment: <InputAdornment position="start">Critical:</InputAdornment>,
                    }}
                    fullWidth
                    margin="normal"
                  />
                )}
                {widget.inputs.includes('high') && (
                  <TextField
                    label="High"
                    type="number"
                    value={inputData.high}
                    onChange={handleInputChange('high')}
                    InputProps={{
                      inputProps: { min: 0 },
                      startAdornment: <InputAdornment position="start">High:</InputAdornment>,
                    }}
                    fullWidth
                    margin="normal"
                  />
                )}
                {widget.inputs.includes('medium') && (
                  <TextField
                    label="Medium"
                    type="number"
                    value={inputData.medium}
                    onChange={handleInputChange('medium')}
                    InputProps={{
                      inputProps: { min: 0 },
                      startAdornment: <InputAdornment position="start">Medium:</InputAdornment>,
                    }}
                    fullWidth
                    margin="normal"
                  />
                )}
                {widget.inputs.includes('low') && (
                  <TextField
                    label="Low"
                    type="number"
                    value={inputData.low}
                    onChange={handleInputChange('low')}
                    InputProps={{
                      inputProps: { min: 0 },
                      startAdornment: <InputAdornment position="start">Low:</InputAdornment>,
                    }}
                    fullWidth
                    margin="normal"
                  />
                )}
              </Box>
            )}
          </React.Fragment>
        ))}
      </List>

      <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
        <Button
          variant="outlined"
          onClick={onCancel}
          sx={{ mr: 2 }}
        >
          Cancel
        </Button>
        <Button
          variant="contained"
          onClick={handleConfirm}
          disabled={!selectedWidget} // Disable confirm if no widget is selected
        >
          Confirm
        </Button>
      </Box>
    </Box>
  );
};

export default AddWidgetForm;
