import React, { useState } from 'react';
import { Container, Typography, Drawer, Box } from '@mui/material';
import Dashboard from './components/Dashboard';
import AddWidgetForm from './components/AddWidgetModal';
import './App.css';

const App = () => {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [widgets, setWidgets] = useState({
    CSPM: [],
    CWPP: [],
    Image: [],
    Ticket: [],
  });

  const openDrawer = () => setIsDrawerOpen(true);
  const closeDrawer = () => setIsDrawerOpen(false);

  const handleConfirm = (selectedWidgets, category) => {
    setWidgets((prevWidgets) => ({
      ...prevWidgets,
      [category]: [...prevWidgets[category], ...selectedWidgets],
    }));
    closeDrawer(); // Close the drawer after confirming
  };

  const handleCancel = () => {
    closeDrawer(); // Close the drawer on cancel
  };

  return (
    <Container
      maxWidth={false}
      sx={{
        padding: '0px 24px',
        '@media (min-width: 1200px)': {
          padding: '0px 48px',
        },
      }}
    >
      <Typography variant="h4" component="h1" gutterBottom>
        Dynamic Dashboard
      </Typography>
      <Dashboard openModal={openDrawer} widgets={widgets} />
      <Drawer
        anchor="right"
        open={isDrawerOpen}
        onClose={closeDrawer}
        sx={{ 
          '& .MuiDrawer-paper': { 
            width: 400,  // Adjust the width of the drawer
            padding: '20px',  // Add padding to match the design
          } 
        }}
      >
        <Box>
          <Typography variant="h6" gutterBottom>
            Personalize your dashboard by adding the following widget
          </Typography>
          {/* Pass handleConfirm and handleCancel to AddWidgetForm */}
          <AddWidgetForm onConfirm={handleConfirm} onCancel={handleCancel} />
        </Box>
      </Drawer>
    </Container>
  );
};

export default App;
