// src/components/Widget.js
import React from 'react';
import { Card, CardContent, Typography, IconButton } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { useStore } from '../store/store';

const Widget = ({ categoryId, widget }) => {
  const removeWidget = useStore((state) => state.removeWidget);

  return (
    <Card variant="outlined" sx={{ minWidth: '300px', maxWidth: '300px', height: '100px', position: 'relative' }}>
      <CardContent>
        <Typography variant="h6">{widget.name}</Typography>
        <Typography variant="body2">{widget.text}</Typography>
      </CardContent>
      <IconButton
        sx={{ position: 'absolute', top: '5px', right: '5px' }}
        onClick={() => removeWidget(categoryId, widget.id)}
      >
        <CloseIcon />
      </IconButton>
    </Card>
  );
};

export default Widget;
