import React, { useState } from 'react';
import { Drawer, Tabs, Tab, Checkbox, List, ListItem, ListItemText, Button, Typography, Box } from '@mui/material';

const AddWidgetForm = ({ isOpen, onClose }) => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [selectedWidgets, setSelectedWidgets] = useState([]);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const handleToggle = (widget) => () => {
    const currentIndex = selectedWidgets.indexOf(widget);
    const newChecked = [...selectedWidgets];

    if (currentIndex === -1) {
      newChecked.push(widget);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setSelectedWidgets(newChecked);
  };

  const widgetOptions = [
    { label: 'Cloud Accounts', category: 'CSPM' },
    { label: 'Cloud Account Risk Assessment', category: 'CSPM' },
    // Add more widgets as needed
  ];

  const filteredWidgets = widgetOptions.filter(widget => {
    switch (selectedTab) {
      case 0:
        return widget.category === 'CSPM';
      case 1:
        return widget.category === 'CWPP';
      case 2:
        return widget.category === 'Image';
      case 3:
        return widget.category === 'Ticket';
      default:
        return false;
    }
  });

  return (
    <Drawer
      anchor="right"
      open={isOpen}
      onClose={onClose}
      sx={{ '& .MuiDrawer-paper': { width: '400px', padding: '20px' } }}
    >
      <Typography variant="h6" gutterBottom>
        Personalize your dashboard by adding the following widget
      </Typography>

      {/* Tabs for switching between categories */}
      <Tabs value={selectedTab} onChange={handleTabChange} textColor="primary" indicatorColor="primary">
        <Tab label="CSPM" />
        <Tab label="CWPP" />
        <Tab label="Image" />
        <Tab label="Ticket" />
      </Tabs>

      {/* List of widgets with checkboxes */}
      <List>
        {filteredWidgets.map((widget) => (
          <ListItem key={widget.label} button onClick={handleToggle(widget.label)}>
            <Checkbox
              checked={selectedWidgets.indexOf(widget.label) !== -1}
              tabIndex={-1}
              disableRipple
            />
            <ListItemText primary={widget.label} />
          </ListItem>
        ))}
      </List>

      {/* Action Buttons */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
        <Button variant="outlined" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="contained" color="primary">
          Confirm
        </Button>
      </Box>
    </Drawer>
  );
};

export default AddWidgetForm;
