import React, { useState } from 'react';
import { Container, Typography } from '@mui/material';
import Dashboard from './components/Dashboard';
import AddWidgetModal from './components/AddWidgetModal';
import './App.css';

const App = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  return (
    <Container
      maxWidth={false} // Ensures that the container spans the entire width of the screen
      sx={{
        padding: '0px 24px', // Ensure the padding is consistent with Material-UI
        '@media (min-width: 1200px)': {
          padding: '0px 48px', // Slightly increase padding for larger screens
        },
      }}
    >
      <Typography variant="h4" component="h1" gutterBottom>
        Dynamic Dashboard
      </Typography>
      <Dashboard openModal={openModal} />
      {isModalOpen && <AddWidgetModal onClose={closeModal} />}
    </Container>
  );
};

export default App;
