import React, { useState } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, Tab, Tabs, Box } from '@mui/material';

// Define the categories and corresponding widgets
const widgetCategories = [
  {
    label: 'CSPM Executive Dashboard',
    widgets: ['Cloud Accounts', 'Cloud Account Risk Assessment'],
  },
  {
    label: 'Registry Scan',
    widgets: ['Image Risk Assessment', 'Image Security Issues'],
  }
];

const AddWidgetModal = ({ onClose, onConfirm }) => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [selectedWidgets, setSelectedWidgets] = useState([]);
  const [inputValues, setInputValues] = useState({});

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const handleWidgetSelection = (widget) => {
    setSelectedWidgets((prev) => {
      if (prev.includes(widget)) {
        return prev.filter((w) => w !== widget);
      } else {
        return [...prev, widget];
      }
    });
  };

  const handleInputChange = (widget, field, value) => {
    setInputValues((prev) => ({
      ...prev,
      [widget]: {
        ...prev[widget],
        [field]: value,
      },
    }));
  };

  const handleConfirm = () => {
    const category = widgetCategories[selectedTab].label;

    const widgetObjects = selectedWidgets.map((widget) => {
      let displayText = '';
      let chartData = {};

      if (widget === 'Cloud Accounts') {
        displayText = `Connected: ${inputValues[widget]?.Connected || 0}, Not Connected: ${inputValues[widget]?.NotConnected || 0}`;
        chartData = { Connected: inputValues[widget]?.Connected || 0, 'Not Connected': inputValues[widget]?.NotConnected || 0 };
      } else if (widget === 'Cloud Account Risk Assessment') {
        displayText = `Failed: ${inputValues[widget]?.Failed || 0}, Passed: ${inputValues[widget]?.Passed || 0}`;
        chartData = { Failed: inputValues[widget]?.Failed || 0, Passed: inputValues[widget]?.Passed || 0 };
      } else if (widget === 'Image Risk Assessment' || widget === 'Image Security Issues') {
        displayText = `Critical: ${inputValues[widget]?.Critical || 0}, High: ${inputValues[widget]?.High || 0}, Medium: ${inputValues[widget]?.Medium || 0}, Low: ${inputValues[widget]?.Low || 0}`;
        chartData = {
          Critical: inputValues[widget]?.Critical || 0,
          High: inputValues[widget]?.High || 0,
          Medium: inputValues[widget]?.Medium || 0,
          Low: inputValues[widget]?.Low || 0
        };
      }

      return {
        id: Date.now(),
        name: widget,
        text: displayText,
        chartData
      };
    });

    onConfirm(widgetObjects, category);
    onClose();
  };

  return (
    <Dialog open onClose={onClose}>
      <DialogTitle>Add Widget</DialogTitle>
      <DialogContent>
        <Tabs value={selectedTab} onChange={handleTabChange}>
          {widgetCategories.map((category, index) => (
            <Tab label={category.label} key={index} />
          ))}
        </Tabs>
        <Box sx={{ marginTop: 2 }}>
          {widgetCategories[selectedTab].widgets.map((widget) => (
            <Box key={widget} sx={{ display: 'flex', alignItems: 'center', marginBottom: 2 }}>
              <input
                type="checkbox"
                checked={selectedWidgets.includes(widget)}
                onChange={() => handleWidgetSelection(widget)}
              />
              <label>{widget}</label>
              {['Cloud Accounts', 'Cloud Account Risk Assessment'].includes(widget) && (
                <>
                  <input
                    type="number"
                    placeholder="Connected"
                    onChange={(e) => handleInputChange(widget, 'Connected', e.target.value)}
                  />
                  <input
                    type="number"
                    placeholder="Not Connected"
                    onChange={(e) => handleInputChange(widget, 'NotConnected', e.target.value)}
                  />
                </>
              )}
              {['Image Risk Assessment', 'Image Security Issues'].includes(widget) && (
                <>
                  <input
                    type="number"
                    placeholder="Critical"
                    onChange={(e) => handleInputChange(widget, 'Critical', e.target.value)}
                  />
                  <input
                    type="number"
                    placeholder="High"
                    onChange={(e) => handleInputChange(widget, 'High', e.target.value)}
                  />
                  <input
                    type="number"
                    placeholder="Medium"
                    onChange={(e) => handleInputChange(widget, 'Medium', e.target.value)}
                  />
                  <input
                    type="number"
                    placeholder="Low"
                    onChange={(e) => handleInputChange(widget, 'Low', e.target.value)}
                  />
                </>
              )}
            </Box>
          ))}
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleConfirm} variant="contained">Confirm</Button>
      </DialogActions>
    </Dialog>
  );
};

export default AddWidgetModal;
