import React from 'react';
import { Paper, IconButton, Typography } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

const Widget = ({ widget, removeWidget }) => {
  return (
    <Paper
      elevation={3}
      sx={{
        padding: 2,
        margin: '0 16px 16px 0', // Add margin to the widget for spacing
        minWidth: 300, // Ensure widgets have a consistent width
        flexShrink: 0, // Prevent shrinking in flexbox layouts
      }}
    >
      <IconButton onClick={() => removeWidget(widget.id)} sx={{ float: 'right' }}>
        <CloseIcon />
      </IconButton>
      <Typography variant="h6">{widget.name}</Typography>
      <Typography>{widget.content}</Typography>
    </Paper>
  );
};

export default Widget;
