import create from 'zustand';  // Importing the 'create' function from zustand

export const useStore = create((set) => ({
  categories: [
    {
      id: 1,
      name: 'CSPM Executive Dashboard',
      widgets: [],
    },
    {
      id: 2,
      name: 'Registry Scan',
      widgets: [],
    },
  ],

  // Add a widget to the specified category
  addWidget: (categoryId, widget) => set((state) => ({
    categories: state.categories.map((category) =>
      category.id === categoryId
        ? { ...category, widgets: [...category.widgets, widget] }
        : category
    ),
  })),

  // Remove a widget from the specified category
  removeWidget: (categoryId, widgetId) => set((state) => ({
    categories: state.categories.map((category) =>
      category.id === categoryId
        ? { ...category, widgets: category.widgets.filter((widget) => widget.id !== widgetId) }
        : category
    ),
  })),

  // Function to update widget data
  updateWidgetData: (categoryId, widgetId, newData) => set((state) => ({
    categories: state.categories.map((category) =>
      category.id === categoryId
        ? {
            ...category,
            widgets: category.widgets.map((widget) =>
              widget.id === widgetId ? { ...widget, ...newData } : widget
            ),
          }
        : category
    ),
  })),
}));

export default useStore;
