import React, { useState } from 'react';
import { Drawer, Box, Checkbox, FormControlLabel, Button, Typography, Tabs, Tab } from '@mui/material';
import { useStore } from '../store/store';

const AddWidgetModal = ({ onClose }) => {
  const { categories, addWidget } = useStore();
  const [selectedCategory, setSelectedCategory] = useState('CSPM');
  const [selectedWidgets, setSelectedWidgets] = useState([]);

  const handleCategoryChange = (event, newValue) => {
    setSelectedCategory(newValue);
    setSelectedWidgets([]); // Reset widgets on category change
  };

  const handleWidgetSelection = (widget) => {
    if (selectedWidgets.includes(widget)) {
      setSelectedWidgets(selectedWidgets.filter((w) => w !== widget));
    } else {
      setSelectedWidgets([...selectedWidgets, widget]);
    }
  };

  const handleAddWidgets = () => {
    selectedWidgets.forEach((widget) => addWidget(selectedCategory, widget));
    onClose();
  };

  const selectedCategoryWidgets = categories.find((c) => c.id === selectedCategory).widgets;

  return (
    <Drawer anchor="right" open onClose={onClose}>
      <Box sx={{ width: 350, padding: 2 }}>
        <Typography variant="h6" gutterBottom>
          Personalize your dashboard by adding the following widget
        </Typography>
        <Tabs
          value={selectedCategory}
          onChange={handleCategoryChange}
          variant="fullWidth"
        >
          <Tab label="CSPM" value="CSPM" />
          <Tab label="CWPP" value="CWPP" />
          <Tab label="Image" value="Image" />
          <Tab label="Ticket" value="Ticket" />
        </Tabs>
        <Box sx={{ marginTop: 2 }}>
          {selectedCategoryWidgets.map((widget) => (
            <FormControlLabel
              key={widget.name}
              control={
                <Checkbox
                  checked={selectedWidgets.includes(widget)}
                  onChange={() => handleWidgetSelection(widget)}
                />
              }
              label={widget.name}
            />
          ))}
        </Box>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: 2 }}>
          <Button variant="contained" onClick={handleAddWidgets}>
            Confirm
          </Button>
          <Button variant="outlined" onClick={onClose}>
            Cancel
          </Button>
        </Box>
      </Box>
    </Drawer>
  );
};

export default AddWidgetModal;
