import React, { useState } from 'react';
import { Tabs, Tab, List, ListItem, ListItemText, Checkbox, Button, Box, Typography, TextField } from '@mui/material';

const AddWidgetForm = ({ onConfirm, onCancel }) => {
  const [selectedTab, setSelectedTab] = useState(0);
  const [selectedWidget, setSelectedWidget] = useState('');
  const [formData, setFormData] = useState({});

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
    setSelectedWidget('');
    setFormData({});
  };

  const handleToggle = (widget) => () => {
    setSelectedWidget(widget);
    setFormData({});
  };

  const widgetCategories = [
    { 
      label: 'CSPM', 
      widgets: [
        { name: 'Cloud Accounts', fields: ['Connected', 'Not Connected'] },
        { name: 'Cloud Account Risk Assessment', fields: ['Failed', 'Passed'] }
      ] 
    },
    { 
      label: 'CWPP', 
      widgets: [
        { name: 'Top 5 Namespace Specific Alerts', fields: ['Data'] },
        { name: 'Workload Alerts', fields: ['Data'] }
      ] 
    },
    { 
      label: 'Image', 
      widgets: [
        { name: 'Image Risk Assessment', fields: ['Critical', 'High', 'Medium', 'Low'] },
        { name: 'Image Security Issues', fields: ['Critical', 'High', 'Medium', 'Low'] }
      ] 
    },
    { 
      label: 'Ticket', 
      widgets: [
        { name: 'Open Tickets', fields: ['Open', 'Closed'] },
        { name: 'Resolved Tickets', fields: ['Resolved', 'Unresolved'] }
      ] 
    },
  ];

  const handleFieldChange = (field, value) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleConfirm = () => {
    const category = widgetCategories[selectedTab].label;
    const widget = {
      name: selectedWidget,
      data: formData,
    };
    onConfirm(widget, category); 
  };

  return (
    <Box sx={{ padding: 2, width: '100%' }}>
      <Tabs
        value={selectedTab}
        onChange={handleTabChange}
        variant="fullWidth"
        sx={{ marginBottom: 2 }}
      >
        {widgetCategories.map((category) => (
          <Tab key={category.label} label={category.label} />
        ))}
      </Tabs>

      <List>
        {widgetCategories[selectedTab].widgets.map((widget) => (
          <ListItem key={widget.name} button onClick={handleToggle(widget.name)}>
            <Checkbox
              checked={selectedWidget === widget.name}
              tabIndex={-1}
              disableRipple
            />
            <ListItemText primary={widget.name} />
          </ListItem>
        ))}
      </List>

      {selectedWidget && (
        <Box sx={{ mt: 2 }}>
          {widgetCategories[selectedTab].widgets
            .find((widget) => widget.name === selectedWidget)
            .fields.map((field) => (
              <Grid container spacing={2} alignItems="center" key={field} sx={{ mb: 2 }}>
                <Grid item xs={4}>
                  <Typography>{field}:</Typography>
                </Grid>
                <Grid item xs={8}>
                  <TextField
                    type="number"
                    value={formData[field] || ''}
                    onChange={(e) => handleFieldChange(field, e.target.value)}
                    InputProps={{ inputProps: { min: 0 } }}
                    fullWidth
                  />
                </Grid>
              </Grid>
            ))}
        </Box>
      )}

      <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
        <Button
          variant="outlined"
          onClick={onCancel}
          sx={{ mr: 2 }}
        >
          Cancel
        </Button>
        <Button
          variant="contained"
          onClick={handleConfirm}
          disabled={!selectedWidget}
        >
          Confirm
        </Button>
      </Box>
    </Box>
  );
};

export default AddWidgetForm;
