import React from 'react';
import { Card, CardContent, Typography, IconButton } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { useStore } from '../store/store';
import PieChart from './PieChart';
import StackedBarChart from './StackedBarChart';

const Widget = ({ categoryId, widget }) => {
  const removeWidget = useStore((state) => state.removeWidget);

  const renderChart = () => {
    if (widget.name === 'Cloud Accounts' || widget.name === 'Cloud Account Risk Assessment') {
      return <PieChart data={widget.chartData} />;
    } else if (widget.name === 'Image Risk Assessment' || widget.name === 'Image Security Issues') {
      return <StackedBarChart data={widget.chartData} />;
    }
    return null;
  };

  return (
    <Card variant="outlined" sx={{ minWidth: '550px', maxWidth: '700px', height: '220px', width: '550px', position: 'relative' }}>
      <CardContent>
        <Typography variant="h6">{widget.name}</Typography>
        <Typography variant="body2">{widget.text}</Typography>
        {renderChart()}
      </CardContent>
      <IconButton
        sx={{ position: 'absolute', top: '5px', right: '5px' }}
        onClick={() => removeWidget(categoryId, widget.id)}
      >
        <CloseIcon />
      </IconButton>
    </Card>
  );
};

export default Widget;
